<?php

namespace AppBundle\Repository;

/**
 * AttributesListSettingsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AttributesListSettingsRepository extends \Doctrine\ORM\EntityRepository
{
    public function updateStatus($id, $status){

        $this->getEntityManager()
            ->createQueryBuilder()
            ->update('AppBundle:AttributesListSettings', 'ad')
            ->set('ad.isEnable', ':st')
            ->where('ad.id = :id')
            ->setParameter('id', $id)
            ->setParameter('st', $status)
            ->getQuery()->execute()
        ;
    }
}
